class Erreur():
    def __init__(self, num = 0, info = None):
        self.num = num
        self.info = info
    
    def getOkErr(self):
        if self.num == 0:
            return u"Ok"
        else:
            return u"Erreur"


    def getMessage(self):
        if self.info != None:
            return ERREURS[self.num] %self.info
        else:
            return ERREURS[self.num]
        
    
