# pour convertir "liste d'indicateurs selectionnés" <--> "texte pour engegistrement"
def toList(txt):
    lst = txt.split()
    l = []
    for i in lst:
        l.append(eval(i))
    return l

def toTxt(lst):
    t = ''
    for i in lst:
        t += str(i) + ' '
    return t

#############################################################################################################
def mergeDict(D, d):
    for K in D.keys():
        if K in d.keys():
            D[K] += d[K]
    for k, v in d.items():
        if not k in D.keys():
            D[k] = v
           
#############################################################################################################
def trier(l):
    """
    """
    #print "trier", l
    if len(l) > 0:
        if '.' in l[0]:
            return sorted(l, key=lambda c: eval(c.split('.')[-1]))
        else:
            try:
                return sorted(l, key=lambda c: eval(c))
            except:
                return sorted(l)
    else:
        return l
   

